function toOnOff(value){
    return value ? "on" : "off";
}

function toBoolean(value){
    return value == "on" ? true : false;
}

function processData(value){

    let parts = value.topic.split("/");
    let measurement = parts[parts.length-1];

    let payload;

    switch (measurement) {
        case 'energy':
        case 'returned_energy':
            // convert energy from watt-min to kwH
            payload = value.payload/60/1000;
            break;
        case 'total':
        case 'total_returned':
            // convert total energy from Wh to kWh
            payload = value.payload/1000;
            break;
        case 'power':
        case 'reactive_power':
            // convert power from W to kW
            payload = value.payload/1000
            break;
        default:
            payload = value.payload; 
    }

    return payload;
}

